import React from 'react';
import './App.css'
import test from "./test-assignment"


function App() {



  return (


    <div className="App">

    <BPP/>

    </div>




  );
}

class BPP extends React.Component{

    render(){

        return this.view();
    }

    view(){
        return (<div>
            <div className="bar"><span className="title">Event </span><span id="teams_place_holder"> </span> <span id="score"> </span><div className="vote"><input id="vote" type="button" value="Vote"/></div></div>
            <div>
                <div className='sport_event'>
                    <div id="user">
                        <div id="what_ami"> </div>
                        <input id="name" type="text" placeholder="Enter your name"/> <input id="cancel" type="button" value="Cancel" /><input id="ok" type="button" value="OK" /></div>

                    <table id="event_items">
                        <tbody>
                        <tr>
                            <th>
                                Away
                            </th>
                            <th>
                                Home
                            </th>

                            <th>
                                Name
                            </th>


                            <th>
                                Group
                            </th>
                            <th>
                                Sport
                            </th>
                            <th>
                                State
                            </th>
                            <th>
                                Country
                            </th>
                            <th>
                                Created date
                            </th>
                            <th>
                                ID
                            </th>
                        </tr>
                        </tbody>



                    </table>
                </div>
            </div>
        </div>);
    }

