import React from 'react';
import logo from './logo.svg';
import poll from './Event'
import './App.css'
import test from "./test-assignment"


function App() {



  return (


    <div className="App">

    <BPP/>

    </div>




  );
}

class BPP extends React.Component{

    render(){

        return (
                <div>
                <div className="bar"><span className="title">Event </span> <div className="vote"><input id="vote" type="button" value="Vote"/></div></div>

                <div>
                    <div className='sport_event'>
                    <table id="event_items">
                        <tbody>
                        <tr>
                            <th>
                                Away
                            </th>
                            <th>
                                Home
                            </th>

                            <th>
                               Name
                            </th>


                            <th>
                                Group
                            </th>
                            <th>
                                Sport
                            </th>
                            <th>
                                State
                            </th>
                            <th>
                                Country
                            </th>
                            <th>
                                Created date
                            </th>
                            <th>
                                ID
                            </th>
                        </tr>
                        </tbody>



                    </table>
                </div>
                </div>
            </div>
        );
    }


    view(val){
        let eventItems=document.querySelector("#event_items");
        eventItems.innerHTML+=`
          <tr>
          <td title="click to select way" class="away_name">
             ${val.awayName}
          </td>
          <td title="click to select way" class = "home_name" >
               ${val.homeName}
              </td>

               <td class = "group" >
                ${val.group}
              </td>

               <td class = "name" >
               ${val.name}
              </td>
               <td class = "sport_type" >
               ${val.sport}
              </td>
               <td class = "state" >
               ${val.state}
              </td>
               <td class = "home_name" >
               ${val.country}
              </td>
               <td class = "create_date" >
              ${val.createdAt}
              </td>
              <td class = "id" >
                 ${val.id}
              </td>

          </tr>

          `
    }
    componentDidMount(){

        let eventList=JSON.parse(JSON.stringify(test));

        let shuffle=Math.floor(Math.random() * Math.floor(5));


        for(let val of eventList){
            if(shuffle.valueOf()===0 && val.sport==="FOOTBALL"){
                this.view(val);

            }else  if(shuffle.valueOf()===1 && val.sport==="SNOOKER"){
                this.view(val);

            } else  if(shuffle.valueOf()===2 && val.sport==="HANDBALL"){
                this.view(val);

            }else if(shuffle.valueOf()===3 && val.sport==="ICE_HOCKEY"){
                this.view(val);

            }else if(shuffle.valueOf()===4 && val.sport==="TENNIS"){
                this.view(val);

            }



           /*<td class="name">
              Date
          </td>
          <td class="group_name" >
              Date
              </td>
          <td class="object_id">
              Date
          </td>
          < td
          className = "id" >
              Date
              </td>
          <td className="sport">
              Date
          </td>
          < td
          className = "state" >
              Date
              </td>
          <td className="country">
              Date
          </td>*/
            //sportEvent.item(0).innerHTML+=val.awayName;
        }

    }


}



export default App;
